package com.example.userschool.view.adapter;import android.content.Context;import android.graphics.Color;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import com.example.userschool.R;import com.example.userschool.model.Absece;import com.example.userschool.view.adapter.viewholder.AbseceStudentRecyclerViewHolder;import java.util.List;public class AbseceStudentRecyclerAdapter extends RecyclerView.Adapter<AbseceStudentRecyclerViewHolder> {    private Context context;    private List<Absece> abseces;    public AbseceStudentRecyclerAdapter(Context context, List<Absece> abseces) {        this.context = context;        this.abseces = abseces;    }    @NonNull    @Override    public AbseceStudentRecyclerViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View view = LayoutInflater.from( context ).inflate( R.layout.item_table_abcese, parent, false );        return new AbseceStudentRecyclerViewHolder( view );    }    @Override    public void onBindViewHolder(@NonNull AbseceStudentRecyclerViewHolder holder, int position) {        holder.getTxtTableLayoutSubject().setText( abseces.get( holder.getAdapterPosition() ).getSbujectName() );        if (abseces.get( holder.getAdapterPosition() ).isFound() == 0)            holder.getImgTableLayoutFound().setImageResource( R.drawable.ic_check_false );        else            holder.getImgTableLayoutFound().setImageResource( R.drawable.ic_check_true );        holder.getTxtTableLayoutDate().setText( abseces.get( holder.getAdapterPosition() ).getDate() );    }    @Override    public int getItemCount() {        return abseces.size();    }    public void setData(List<Absece> abseces) {        this.abseces = abseces;        notifyDataSetChanged();    }}