package com.example.userschool.view.adapter;import android.content.Context;import android.content.Intent;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import com.example.userschool.R;import com.example.userschool.model.ClassSchool;import com.example.userschool.view.activity.ChoseClassByTeacherActivity;import com.example.userschool.view.activity.TeacherActivity;import com.example.userschool.view.adapter.viewholder.ClassChoseByTeacherViewHolder;import java.util.ArrayList;import java.util.List;public class ClassChoseByTeacherRecyclerAdapter extends RecyclerView.Adapter<ClassChoseByTeacherViewHolder> {    private List<ClassSchool> classSchools;    private Context context;    public ClassChoseByTeacherRecyclerAdapter(List<ClassSchool> classSchools, Context context) {        this.classSchools = classSchools;        this.context = context;    }    @NonNull    @Override    public ClassChoseByTeacherViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View view = LayoutInflater.from( context ).inflate( R.layout.item_recycler_class, parent, false );        return new ClassChoseByTeacherViewHolder( view );    }    @Override    public void onBindViewHolder(@NonNull ClassChoseByTeacherViewHolder holder, int position) {        holder.getTxtRecyclerClassName().setText( classSchools.get( holder.getAdapterPosition() ).getName() );        holder.getTxtRecyclerClassNumber().setText( classSchools.get( holder.getAdapterPosition() ).getCountStudent() + "" );        holder.getView().setOnClickListener( new View.OnClickListener() {            @Override            public void onClick(View view) {                Intent intent = new Intent( context, TeacherActivity.class );                intent.putExtra( "classID", classSchools.get( holder.getAdapterPosition() ).getId() );                ((ChoseClassByTeacherActivity) context).startActivity( intent );            }        } );    }    @Override    public int getItemCount() {        return classSchools.size();    }    public void setData(List<ClassSchool> classSchools) {        this.classSchools = classSchools;        notifyDataSetChanged();    }}