package com.example.userschool.view.adapter;import android.annotation.SuppressLint;import android.content.Context;import android.graphics.Color;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import com.example.userschool.R;import com.example.userschool.model.Marks;import com.example.userschool.view.adapter.viewholder.MarkStudentViewHolder;import java.util.List;public class MarkStudentRecyclerAdapter extends RecyclerView.Adapter<MarkStudentViewHolder> {    private Context context;    private List<Marks> marks;    public MarkStudentRecyclerAdapter(Context context, List<Marks> marks) {        this.context = context;        this.marks = marks;    }    @NonNull    @Override    public MarkStudentViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        View view = LayoutInflater.from( context ).inflate( R.layout.item_table_marks, parent, false );        return new MarkStudentViewHolder( view );    }    @SuppressLint("ResourceAsColor")    @Override    public void onBindViewHolder(@NonNull MarkStudentViewHolder holder, int position) {        holder.getTxtTableLayoutSubject().setText( marks.get( holder.getAdapterPosition() ).getSubjcetName() );        holder.getTxtTableLayoutPractical().setText( marks.get( holder.getAdapterPosition() ).getPractical() + "" );        if (marks.get( holder.getAdapterPosition() ).getPractical() < 15)            holder.getTxtTableLayoutPractical().setTextColor( Color.parseColor( "#B65736" ) );        holder.getTxtTableLayoutThearitical().setText( marks.get( holder.getAdapterPosition() ).getThearitical() + "" );        if (marks.get( holder.getAdapterPosition() ).getThearitical() < 30)            holder.getTxtTableLayoutThearitical().setTextColor( Color.parseColor( "#B65736" ) );        holder.getTxtTableLayoutSum().setText( marks.get( holder.getAdapterPosition() ).getSum() + "" );        if (marks.get( holder.getAdapterPosition() ).getSum() < 60)            holder.getTxtTableLayoutSum().setTextColor( Color.parseColor( "#B65736" ) );    }    @Override    public int getItemCount() {        return marks.size();    }    public void setData(List<Marks> marks) {        this.marks = marks;        notifyDataSetChanged();    }}