package com.example.userschool.view.adapter.viewholder;import android.view.View;import android.widget.CheckBox;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import com.example.userschool.R;public class AbseceTeacherRecyclerViewHolder extends RecyclerView.ViewHolder {    private View view;    private TextView txtTableLayoutStudentName;    private CheckBox checkboxTableLayoutIsFound;    public AbseceTeacherRecyclerViewHolder(@NonNull View view) {        super( view );        this.view = view;        txtTableLayoutStudentName = view.findViewById( R.id.txtTableLayoutStudentName );        checkboxTableLayoutIsFound = view.findViewById( R.id.checkboxTableLayoutIsFound );    }    public View getView() {        return view;    }    public TextView getTxtTableLayoutStudentName() {        return txtTableLayoutStudentName;    }    public CheckBox getCheckboxTableLayoutIsFound() {        return checkboxTableLayoutIsFound;    }}