package com.example.userschool.view.adapter.viewholder;import android.view.View;import android.widget.RelativeLayout;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import com.example.userschool.R;import org.w3c.dom.Text;public class ClassChoseByTeacherViewHolder extends RecyclerView.ViewHolder {    private View view;    private TextView txtRecyclerClassName;    private TextView txtRecyclerClassNumber;    public ClassChoseByTeacherViewHolder(@NonNull View view) {        super( view );        this.view = view;        txtRecyclerClassName = view.findViewById( R.id.txtRecyclerClassName );        txtRecyclerClassNumber = view.findViewById( R.id.txtRecyclerClassNumber );    }    public View getView() {        return view;    }    public TextView getTxtRecyclerClassNumber() {        return txtRecyclerClassNumber;    }    public TextView getTxtRecyclerClassName() {        return txtRecyclerClassName;    }}