package com.example.userschool.view.adapter.viewholder;import android.view.View;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import com.example.userschool.R;public class MarkStudentViewHolder extends RecyclerView.ViewHolder {    private View view;    private TextView txtTableLayoutSubject;    private TextView txtTableLayoutPractical;    private TextView txtTableLayoutThearitical;    private TextView txtTableLayoutSum;    public MarkStudentViewHolder(@NonNull View view) {        super( view );        this.view = view;        txtTableLayoutSubject = view.findViewById( R.id.txtTableLayoutSubject );        txtTableLayoutPractical = view.findViewById( R.id.txtTableLayoutPractical );        txtTableLayoutThearitical = view.findViewById( R.id.txtTableLayoutThearitical );        txtTableLayoutSum = view.findViewById( R.id.txtTableLayoutSum );    }    public View getView() {        return view;    }    public TextView getTxtTableLayoutSubject() {        return txtTableLayoutSubject;    }    public TextView getTxtTableLayoutPractical() {        return txtTableLayoutPractical;    }    public TextView getTxtTableLayoutThearitical() {        return txtTableLayoutThearitical;    }    public TextView getTxtTableLayoutSum() {        return txtTableLayoutSum;    }}