package com.example.userschool.view.adapter.viewholder;import android.view.View;import android.widget.EditText;import android.widget.TextView;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import com.example.userschool.R;public class MarkTeacherViewHolder extends RecyclerView.ViewHolder {    private View view;    private TextView txtrecyclerFragmentMarksTeacherStudent;    private EditText edtrecyclerFragmentMarksTeacherPractical;    private EditText edtrecyclerFragmentMarksTeacherThearitical;    public MarkTeacherViewHolder(@NonNull View view) {        super( view );        this.view = view;        txtrecyclerFragmentMarksTeacherStudent = view.findViewById( R.id.txtrecyclerFragmentMarksTeacherStudent );        edtrecyclerFragmentMarksTeacherPractical = view.findViewById( R.id.edtrecyclerFragmentMarksTeacherPractical );        edtrecyclerFragmentMarksTeacherThearitical = view.findViewById( R.id.edtrecyclerFragmentMarksTeacherThearitical );    }    public View getView() {        return view;    }    public TextView getTxtrecyclerFragmentMarksTeacherStudent() {        return txtrecyclerFragmentMarksTeacherStudent;    }    public EditText getEdtrecyclerFragmentMarksTeacherPractical() {        return edtrecyclerFragmentMarksTeacherPractical;    }    public EditText getEdtrecyclerFragmentMarksTeacherThearitical() {        return edtrecyclerFragmentMarksTeacherThearitical;    }}